import React, { useState, useEffect } from 'react';

import Header from './components/Header';
import Cards from './components/Cards';
import Footer from './components/Footer';

import './App.css';

//5db94f24
const api_url = 'http://www.omdbapi.com?apikey=5db94f24';

function App() {
  const [movies, setMovies] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');

  useEffect(() => {
    searchMovies('shrek');
  }, []);

  const searchMovies = async (title) => {
    const res = await fetch(`${api_url}&s=${title}`);
    const data = await res.json();

    setMovies(data.Search);
  };

  function handleClick() {
    searchMovies(searchTerm);
  }

  function handleChange() {
    setSearchTerm(document.getElementById('search-bar').value);
  }

  const cardList = () => {
    <div></div>;
  };

  return (
    <div className='app'>
      <Header handleClick={handleClick} handleChange={handleChange} />
      <div className='card-container'>{cardList}</div>
      <Footer />
    </div>
  );
}

export default App;
