import React, { useState, useEffect } from 'react';

import Header from './components/Header';
import Cards from './components/Cards';
import Footer from './components/Footer';

import './App.css';

//5db94f24
const api_url = 'http://www.omdbapi.com?apikey=5db94f24';

function App() {
  const [movies, setMovies] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');

  useEffect(() => {
    searchMovies('');
  }, []);

  const searchMovies = async (title) => {
    const res = await fetch(`${api_url}&s=${title}`);
    const data = await res.json();

    setMovies(data.Search);
  };

  function handleClick() {
    searchMovies(searchTerm);
  }

  function handleChange() {
    setSearchTerm(document.getElementById('search-bar').value);
  }

  function cardList() {
    movies.map((movie) => {
      <Cards poster={movie.Poster} title={movie.Title} year={movie.Year} />;
    });
  }

  return (
    <div className='app'>
      <Header handleClick={handleClick} handleChange={handleChange} />
      <div className='card-container'>
        <Cards
          poster={movies[0].Poster}
          title={movies[0].Title}
          year={movies[0].Year}
        />
      </div>
      <Footer />
    </div>
  );
}

export default App;
